toolScript.printScript:


progress.setSteps(brie.meshes.length*2 + 3);

slicer.setSliceHeight(brie.sliceHeight);
pather.set("PathWidth", brie.pathWidth);
for (var i = 0; i < brie.meshes.length; ++i) {
progress.log("Slicing brie Mesh");
  slicer.doSlicing(brie.meshes[i]);
  progress.step();
  progress.log("Pathing brie Mesh");
  pather.doPathing(brie.meshes[i]);
  progress.step();
}

var brieMaterialCalibration = {
  pathSpeed: brie.pathSpeed,
  depositionRate: brie.depositionRate,
  pathWidth: brie.pathWidth,
  depositionRate: brie.depositionRate,
  pushout: brie.pushout,
  suckback: brie.suckback,
  suckbackDelay: brie.suckbackDelay,
  clearance: brie.clearance,
  pausePaths: brie.pausePaths,
  pitch: brie.pitch
};




var fabWriter = fabFile.fabAtHomeModel2Writer();
fabWriter.addMeshes("brie", brieMaterialCalibration, brie.meshes);
progress.step();

fabWriter.sortBottomUp();;
fabWriter.print();

progress.finish();
