toolScript.printScript:


progress.setSteps(silicone25p.meshes.length*2 + 3);

slicer.setSliceHeight(silicone25p.sliceHeight);
pather.set("PathWidth", silicone25p.pathWidth);
pather.set("density", 0.25);
for (var i = 0; i < silicone25p.meshes.length; ++i) {
  progress.log("Slicing silicone25p Mesh");
  slicer.doSlicing(silicone25p.meshes[i]);
  progress.step();
  progress.log("Pathing silicone25p Mesh");
  pather.doPathing(silicone25p.meshes[i]);
  progress.step();
}

var silicone25pMaterialCalibration = {
  pathSpeed: silicone25p.pathSpeed,
  depositionRate: silicone25p.depositionRate,
  pathWidth: silicone25p.pathWidth,
  depositionRate: silicone25p.depositionRate,
  pushout: silicone25p.pushout,
  suckback: silicone25p.suckback,
  suckbackDelay: silicone25p.suckbackDelay,
  clearance: silicone25p.clearance,
  pausePaths: silicone25p.pausePaths,
  pitch: silicone25p.pitch
};

var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("silicone25p", silicone25pMaterialCalibration, silicone25p.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
