toolScript.printScript:


progress.setSteps(GreenTipValve.meshes.length*2 + 3);

slicer.setSliceHeight(GreenTipValve.sliceHeight);
pather.set("PathWidth", GreenTipValve.pathWidth);
pather.set("density",0.5);
for (var i = 0; i < GreenTipValve.meshes.length; ++i) {
  progress.log("Slicing GreenTipValve Mesh");
  slicer.doSlicing(GreenTipValve.meshes[i]);
  progress.step();
  progress.log("Pathing GreenTipValve Mesh");
  pather.doPathing(GreenTipValve.meshes[i]);
  progress.step();
}

var GreenTipValveMaterialCalibration = {
  pathSpeed: GreenTipValve.pathSpeed,
  depositionRate: 0.0,
  pathWidth: GreenTipValve.pathWidth,
  depositionRate: GreenTipValve.depositionRate,
  pushout: GreenTipValve.pushout,
  suckback: GreenTipValve.suckback,
  suckbackDelay: GreenTipValve.suckbackDelay,
  clearance: GreenTipValve.clearance,
  pausePaths: GreenTipValve.pausePaths,
  pitch: GreenTipValve.pitch
};

var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("GreenTipValve", GreenTipValveMaterialCalibration, GreenTipValve.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
