toolScript.printScript:


progress.setSteps(smallmasa.meshes.length*2 + 3);

slicer.setSliceHeight(smallmasa.sliceHeight);
pather.set("PathWidth", smallmasa.pathWidth);
for (var i = 0; i < smallmasa.meshes.length; ++i) {
  progress.log("Slicing Masa Mesh");
  slicer.doSlicing(smallmasa.meshes[i]);
  progress.step();
  progress.log("Pathing Masa Mesh");
  pather.doPathing(smallmasa.meshes[i]);
  progress.step();
}

var smallmasaMaterialCalibration = {
  pathSpeed: smallmasa.pathSpeed,
  depositionRate: smallmasa.depositionRate,
  pathWidth: smallmasa.pathWidth,
  depositionRate: smallmasa.depositionRate,
  pushout: smallmasa.pushout,
  suckback: smallmasa.suckback,
  suckbackDelay: smallmasa.suckbackDelay,
  clearance: smallmasa.clearance,
  pausePaths: smallmasa.pausePaths,
  pitch: smallmasa.pitch
};

var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("smallmasa", smallmasaMaterialCalibration, smallmasa.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
