toolScript.printScript:


progress.setSteps(GreenTipValveStochastic.meshes.length*2 + 3);

slicer.setSliceHeight(GreenTipValveStochastic.sliceHeight);
pather.set("PathWidth", GreenTipValveStochastic.pathWidth);
pather.set("density",0.5);
for (var i = 0; i < GreenTipValveStochastic.meshes.length; ++i) {
  progress.log("Slicing GreenTipValveStochastic Mesh");
  slicer.doSlicing(GreenTipValveStochastic.meshes[i]);
  progress.step();
  progress.log("Pathing GreenTipValveStochastic Mesh");
  pather.doPathing(GreenTipValveStochastic.meshes[i]);
  progress.step();
}

var GreenTipValveStochasticMaterialCalibration = {
  pathSpeed: GreenTipValveStochastic.pathSpeed,
  depositionRate: 0.0,
  pathWidth: GreenTipValveStochastic.pathWidth,
  depositionRate: GreenTipValveStochastic.depositionRate,
  pushout: GreenTipValveStochastic.pushout,
  suckback: GreenTipValveStochastic.suckback,
  suckbackDelay: GreenTipValveStochastic.suckbackDelay,
  clearance: GreenTipValveStochastic.clearance,
  pausePaths: GreenTipValveStochastic.pausePaths,
  pitch: GreenTipValveStochastic.pitch
};

var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("GreenTipValveStochastic", GreenTipValveStochasticMaterialCalibration, GreenTipValveStochastic.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
