toolScript.printScript:


progress.setSteps(ValveStochastic13.meshes.length*2 + 3);

slicer.setSliceHeight(ValveStochastic13.sliceHeight);
pather.set("PathWidth", ValveStochastic13.pathWidth);
pather.set("density",0.5);
for (var i = 0; i < ValveStochastic13.meshes.length; ++i) {
  progress.log("Slicing ValveStochastic13 Mesh");
  slicer.doSlicing(ValveStochastic13.meshes[i]);
  progress.step();
  progress.log("Pathing ValveStochastic13 Mesh");
  pather.doPathing(ValveStochastic13.meshes[i]);
  progress.step();
}

var ValveStochastic13MaterialCalibration = {
  pathSpeed: ValveStochastic13.pathSpeed,
  depositionRate: 0.0,
  pathWidth: ValveStochastic13.pathWidth,
  depositionRate: ValveStochastic13.depositionRate,
  pushout: ValveStochastic13.pushout,
  suckback: ValveStochastic13.suckback,
  suckbackDelay: ValveStochastic13.suckbackDelay,
  clearance: ValveStochastic13.clearance,
  pausePaths: ValveStochastic13.pausePaths,
  pitch: ValveStochastic13.pitch
};

var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("ValveStochastic13", ValveStochastic13MaterialCalibration, ValveStochastic13.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
