toolScript.printScript:


progress.setSteps(Masa.meshes.length*2+1);

slicer.setSliceHeight(Masa.sliceHeight);
pather.set("PathWidth", Masa.pathWidth);
for (var i = 0; i < Masa.meshes.length; ++i) {
  progress.log("Slicing Masa Mesh");
  slicer.doSlicing(Masa.meshes[i]);
  progress.step();
  progress.log("Pathing Masa Mesh");
  pather.doPathing(Masa.meshes[i]);
  progress.step();
}



var MasaMaterialCalibration = {
  pathSpeed: Masa.pathSpeed,
  depositionRate: 0.0,
  pathWidth: Masa.pathWidth,
  depositionRate: Masa.depositionRate,
  pushout: Masa.pushout,
  suckback: Masa.suckback,
  suckbackDelay: Masa.suckbackDelay,
  clearance: Masa.clearance,
  pausePaths: Masa.pausePaths,
  pitch: Masa.pitch
};


var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("Masa", MasaMaterialCalibration, Masa.meshes);
progress.step();


fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
