toolScript.printScript:


progress.setSteps(iceCream.meshes.length*2 + cCookie.meshes.length*2+ 3);

slicer.setSliceHeight(iceCream.sliceHeight);
pather.set("PathWidth", iceCream.pathWidth);
for (var i = 0; i < iceCream.meshes.length; ++i) {
  progress.log("Slicing iceCream Mesh");
  slicer.doSlicing(iceCream.meshes[i]);
  progress.step();
  progress.log("Pathing iceCream Mesh");
  pather.doPathing(iceCream.meshes[i]);
  progress.step();
}

slicer.setSliceHeight(cCookie.sliceHeight);
pather.set("PathWidth", cCookie.pathWidth);
for (var i = 0; i < cCookie.meshes.length; ++i) {
  progress.log("Slicing cCookie Mesh");
  slicer.doSlicing(cCookie.meshes[i]);
  progress.step();
  progress.log("Pathing cCookie Mesh");
  pather.doPathing(cCookie.meshes[i]);
  progress.step();
}


var iceCreamMaterialCalibration = {
  pathSpeed: iceCream.pathSpeed,
  depositionRate: 0.0,
  pathWidth: iceCream.pathWidth,
  depositionRate: iceCream.depositionRate,
  pushout: iceCream.pushout,
  suckback: iceCream.suckback,
  suckbackDelay: iceCream.suckbackDelay,
  clearance: iceCream.clearance,
  pausePaths: iceCream.pausePaths,
  pitch: iceCream.pitch
};

var cCookieMaterialCalibration = {
  pathSpeed: cCookie.pathSpeed,
  depositionRate: 0.0,
  pathWidth: cCookie.pathWidth,
  depositionRate: cCookie.depositionRate,
  pushout: cCookie.pushout,
  suckback: cCookie.suckback,
  suckbackDelay: cCookie.suckbackDelay,
  clearance: cCookie.clearance,
  pausePaths: cCookie.pausePaths,
  pitch: cCookie.pitch
};

var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("iceCream", iceCreamMaterialCalibration, iceCream.meshes);
progress.step();

fabWriter.addMeshes("cCookie", cCookieMaterialCalibration, cCookie.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
