toolScript.printScript:

function makeCalib(x){
  x.CompressionVolume = (x.pushout+x.suckback)*.5*5000*x.pitch;
  x.AreaConstant  = x.depositionRate*3.14159265*64/x.sliceHeight/x.pathWidth;

  y={pathSpeed: x.pathSpeed,
	 pathHieght: x.sliceHeight,
	 pathWidth: x.pathWidth,
	 areaConstant: x.AreaConstant,
	 compressionVolume: x.CompressionVolume
	}
  return y;
}
progress.setSteps(epoxy.meshes.length*2 + masonSilicone.meshes.length*2 + 5);

slicer.setSliceHeight(epoxy.sliceHeight);
pather.set("PathWidth", epoxy.pathWidth);
for (var i = 0; i < epoxy.meshes.length; ++i) {
  progress.log("Slicing Epoxy Mesh");
  slicer.doSlicing(epoxy.meshes[i]);
  progress.step();
  progress.log("Pathing Epoxy Mesh");
  pather.doPathing(epoxy.meshes[i]);
  progress.step();
}

slicer.setSliceHeight(masonSilicone.sliceHeight);
pather.set("PathWidth", masonSilicone.pathWidth);
for (var i = 0; i < masonSilicone.meshes.length; ++i) {
  progress.log("Slicing Silicone Mesh");
  slicer.doSlicing(masonSilicone.meshes[i]);
  progress.step();
  progress.log("Pathing Silicone Mesh");
  pather.doPathing(masonSilicone.meshes[i]);
  progress.step();
}

var epoxyMaterialCalibration = makeCalib(epoxy);

var masonSiliconeMaterialCalibration = makeCalib(masonSilicone);

var fabWriter = fabFile.fabAtHomeModel2Writer();

fabWriter.addMeshes("epoxy", epoxyMaterialCalibration, epoxy.meshes);
progress.step();
fabWriter.addMeshes("masonSilicone", masonSiliconeMaterialCalibration, masonSilicone.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.setPrintAcceleration(printAcceleration);

progress.step();
fabWriter.print();

progress.finish();
