toolScript.printScript:

function makeCalib(x){
  x.CompressionVolume = (x.pushout+x.suckback)*.5*5000*x.pitch;
  x.AreaConstant  = x.depositionRate*3.14159265*64/x.sliceHeight/x.pathWidth;

  y={pathSpeed: x.pathSpeed,
	 pathHieght: x.sliceHeight,
	 pathWidth: x.pathWidth,
	 areaConstant: x.AreaConstant,
	 compressionVolume: x.CompressionVolume
	}
  return y;
}

progress.setSteps(greenTaperSilicone.meshes.length*2 + frosting.meshes.length*2 + 3);

slicer.setSliceHeight(greenTaperSilicone.sliceHeight);
pather.set("PathWidth", greenTaperSilicone.pathWidth);
for (var i = 0; i < greenTaperSilicone.meshes.length; ++i) {
  progress.log("Slicing Silicone Mesh");
  slicer.doSlicing(greenTaperSilicone.meshes[i]);
  progress.step();
  progress.log("Pathing Silicone Mesh");
  pather.doPathing(greenTaperSilicone.meshes[i]);
  progress.step();
}

slicer.setSliceHeight(frosting.sliceHeight);
pather.set("PathWidth", frosting.pathWidth);
for (var i = 0; i < frosting.meshes.length; ++i) {
progress.log("Slicing Frosting Mesh");
  slicer.doSlicing(frosting.meshes[i]);
  progress.step();
  progress.log("Pathing Frosting Mesh");
  pather.doPathing(frosting.meshes[i]);
  progress.step();
}

var greenTaperSiliconeMaterialCalibration = makeCalib(greenTaperSilicone);

var frostingMaterialCalibration = makeCalib(frosting);

var fabWriter = fabFile.fabAtHomeModel2Writer();
fabWriter.addMeshes("greenTaperSilicone", greenTaperSiliconeMaterialCalibration, greenTaperSilicone.meshes);
progress.step();
fabWriter.addMeshes("frosting", frostingMaterialCalibration, frosting.meshes);
progress.step();

fabWriter.sortBottomUp();
fabWriter.print();

progress.finish();
