toolScript.printScript:

function makeCalib(x){
  x.CompressionVolume = (x.pushout+x.suckback)*.5*5000*x.pitch;
  x.AreaConstant  = x.depositionRate*3.14159265*64/x.sliceHeight/x.pathWidth;

  y={pathSpeed: x.pathSpeed,
	 pathHieght: x.sliceHeight,
	 pathWidth: x.pathWidth,
	 areaConstant: x.AreaConstant,
	 compressionVolume: x.CompressionVolume
	}
  return y;
}


progress.setSteps(wood.meshes.length*2 + 3);

slicer.setSliceHeight(wood.sliceHeight);
pather.set("PathWidth", wood.pathWidth);
for (var i = 0; i < wood.meshes.length; ++i) {
progress.log("Slicing wood Mesh");
  slicer.doSlicing(wood.meshes[i]);
  progress.step();
  progress.log("Pathing wood Mesh");
  pather.doPathing(wood.meshes[i]);
  progress.step();
}

var woodMaterialCalibration = makeCalib(wood);

var fabWriter = fabFile.fabAtHomeModel2Writer();
fabWriter.addMeshes("wood", woodMaterialCalibration, wood.meshes);
progress.step();

fabWriter.sortTopDown();
fabWriter.print();

progress.finish();
